#!/bin/bash

# pre-requisito: configurar device remoto com ADB via WI-FI

# listar todas as apps:
# pm list packages |grep zebra

# Listar todas as activities de uma app:
# dumpsys package | grep -Eo "^[[:space:]]+[0-9a-f]+[[:space:]]+com.symbol.wfc.voice/[^[:space:]]+" | grep -oE "[^[:space:]]+$"

echo 'desbloqueando dispositivo'
adb shell input keyevent 26 && adb shell input keyevent 82 && sleep 3 && adb shell input text 6845 && adb shell input keyevent 66

echo 'iniciando DroidCam no device remoto'
adb shell 'am start com.dev47apps.droidcam/.DroidCam'

echo 'recuperando ip do dispositivo'
cameraAddress=$(adb shell "ip addr show wlan0 | grep 'inet ' | awk '{print \$2}' | cut -d'/' -f 1")
cameraAddress+=':4747'

sleep 3

echo 'iniciando feed'
ffplay 'http://'$cameraAddress'/video' &
videoFeed=$!

echo 'focando a camera'
curl $cameraAddress'/cam/1/af'
sleep 2

echo 'fazendo download do frame'
wget -O $(date +'%Y-%m-%d--%H-%M-%S').jpg $cameraAddress'/cam/1/frame.jpg'

echo 'finalizando feed'
kill $videoFeed

echo 'bloqueando dispositivo                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    